    {
        echo "i am duck";
    }
}

class elephant{
    function __construct()
    {
        echo "i am elephant";
    }
}

class plant{
    public static function create($name){
        if(class_exists($name)){
            return new $name;
        }else{
            die("not a class");
          }
    }
}

plant::create('elephant'); //i am elephant
plant::create('duck');   //i am duck
plant::create('1');     //not a class

