//树结构的遍历
function traverseStructure($iterator)
{
    while ($iterator->valid()) {
        if ($iterator->hasChildren()) {
            traverseStructure($iterator->getChildren());
        } else {
            echo $iterator->key() . ' : ' . $iterator->current() . PHP_EOL;
        }
        $iterator->next();
    }
}

