二分查询
function search_in_array($arr, $val , $flag = 1){
    static $num = 1;
    static $sum = 0;

    $sum += ceil(count($arr) / pow(2,$num)) * $flag;

    $offset = count($arr) - $sum;

    if (!isset($arr[$offset])){
        return false;
    }

    echo "{$offset}\n";

    $num++;

    if ($arr[$offset] === $val) {
        return $offset;
    } elseif ($arr[$offset]  $val){
        return search_in_array($arr , $val , 1);
    }

    return false;
}

